import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  Alert,
  ActivityIndicator
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { login } from '../services/api';

export default function LoginScreen({ navigation, onLoginSuccess  }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    if (!email || !password) {
      Alert.alert('Error', 'Please enter email and password');
      return;
    }

    setLoading(true);
    try {
      const response = await login(email, password);
      
    if (response.data.success) {
  await AsyncStorage.setItem('sessionToken', response.data.sessionToken);
  await AsyncStorage.setItem('userId', response.data.userId);
  
  // Trigger login state change
  if (route.params?.onLogin) {
    route.params.onLogin();
  }
}
    } catch (error) {
      const message = error.response?.data?.message || 'Login failed. Please try again.';
      Alert.alert('Login Failed', message);
    } finally {
      setLoadingContinue01:15(false);
}
};return (
<View style={styles.container}>
<View style={styles.header}>
<Text style={styles.title}>HealthVault</Text>
<Text style={styles.subtitle}>Your Personal Health Record</Text>
</View>  <View style={styles.form}>
    <TextInput
      style={styles.input}
      placeholder="Email"
      value={email}
      onChangeText={setEmail}
      autoCapitalize="none"
      keyboardType="email-address"
    />    <TextInput
      style={styles.input}
      placeholder="Password"
      value={password}
      onChangeText={setPassword}
      secureTextEntry
    />    <TouchableOpacity
      style={styles.button}
      onPress={handleLogin}
      disabled={loading}
    >
      {loading ? (
        <ActivityIndicator color="#fff" />
      ) : (
        <Text style={styles.buttonText}>Login</Text>
      )}
    </TouchableOpacity>    <TouchableOpacity
      style={styles.linkButton}
      onPress={() => navigation.navigate('Register')}
    >
      <Text style={styles.linkText}>
        Don't have an account? Register
      </Text>
    </TouchableOpacity>
  </View>
</View>
);
}const styles = StyleSheet.create({
container: {
flex: 1,
backgroundColor: '#f5f5f5',
justifyContent: 'center',
padding: 20
},
header: {
alignItems: 'center',
marginBottom: 50
},
title: {
fontSize: 32,
fontWeight: 'bold',
color: '#4A90E2',
marginBottom: 10
},
subtitle: {
fontSize: 16,
color: '#666'
},
form: {
width: '100%'
},
input: {
backgroundColor: '#fff',
padding: 15,
borderRadius: 8,
marginBottom: 15,
fontSize: 16,
borderWidth: 1,
borderColor: '#ddd'
},
button: {
backgroundColor: '#4A90E2',
padding: 15,
borderRadius: 8,
alignItems: 'center',
marginTop: 10
},
buttonText: {
color: '#fff',
fontSize: 16,
fontWeight: 'bold'
},
linkButton: {
marginTop: 20,
alignItems: 'center'
},
linkText: {
color: '#4A90E2',
fontSize: 14
}
});
